mapper.insert:
begin
	insert into member(SEQ,NAME,ID,PWD,EMAIL1,EMAIL2,TEL1,TEL2,TEL3,REGISTERDATE) 
			values(member_seq.nextval,#{name},#{id},#{pwd},#{email1},#{email2},#{tel1},#{tel2},#{tel3},SYSDATE);	
	insert into authorities(username,authority) values(#{id},'ROLE_USER');						
end;

mapper.update:
update member set name=#{name}
				 ,pwd=#{pwd}
				 ,email1=#{email1}
				 ,email2=#{email2}
				 ,tel1=#{tel1}
				 ,tel2=#{tel2}
				 ,tel3=#{tel3}
				 ,zipcode=#{zipcode}
				 ,addr1=#{addr1}
				 ,addr2=#{addr2}
				 where id=#{id}
